import { Component } from '@angular/core';
import {WebserviceService} from "../service/webservice.service";

@Component({
  selector: 'login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(public webService:WebserviceService) {  }
  loginResp:string="";
  pwd: any = "jimbo";
  mail: any = "happylion915";

  async login() {
    await this.webService.login("login",this.mail,this.pwd);
    this.loginResp=this.webService.loginResponse;
    console.log(this.loginResp);
  }
}
<div class="container">
  <div class="form-group">
    <label for="exampleInputEmail1">Email address</label>
    <input [(ngModel)]="mail" type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email">
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input [(ngModel)]="pwd" type="password" class="form-control" id="exampleInputPassword1" placeholder="Password">
  </div>
  <button type="button" class="btn btn-primary mt-4 mb-5 w-100" (click)="login()">Login</button>
</div>
