import {Component, OnInit} from '@angular/core';
import {WebserviceService} from "../service/webservice.service";
import {single} from "rxjs";

@Component({
  selector: 'people',
  templateUrl: './people.component.html',
  styleUrls: ['./people.component.css']
})
export class PeopleComponent implements OnInit{
  constructor(public webService:WebserviceService) {   }

  lstPeople:any;
  esami:any;

  async ngOnInit(){
    await this.webService.getPeople("getPeople");
    this.lstPeople=this.webService.peopleData;
    console.log("list people:"+this.lstPeople[0]._id);
    console.log(JSON.stringify(this.lstPeople));

  }

  async getExams(_id: any) {
    await this.webService.getExams("getEsami",_id);
    this.esami=this.webService.datiEsami[0];
    console.log(this.esami);
  }
}
<div>
  <div class="row">
    <div class="col-sm-7">
      <div *ngFor="let person of lstPeople">
        <h5 class="alert alert-info mt-3 mb-3 w-100" (click)="getExams(person._id)">{{person._id}} - {{person.gender}} - {{person.first}} {{person.last}} - {{person.email}}</h5>
      </div>
    </div>
    <div class="col-sm-5">
      <h5 class=" mt-3 mb-3 w-100 bg-warning" *ngIf="webService.esamiOk">
        <div>{{esami._id}}</div>
        <div>{{esami._id_stud}}</div>
        <div>{{esami.num_esami}}</div>
        <div>{{esami.media}}</div>
      </h5>
    </div>
  </div>
</div>
